import styled from "styled-components";

export const StyledGameBord = styled("div")`
  display: flex;
`;

export const StyledBordCell = styled("div")`
  display: flex;
  align-content: center;
  justify-content: center;
  align-items: center;
  width: 80px;
  height: 80px;
  border: solid 1px blue;
  ${(p) => {
    switch (p.type) {
      case "snake":
        return "background: green;";
      case "food":
        return "background: green;";
    }
  }}
`;

