      window.removeEventListener("keydown", handleMove);
      document.body.style.overflow = "auto";
    };
  });
  useEffect(() => {
    if (snake.head.val.x === food.x && snake.head.val.y === food.y) {
      snakeCells.add([snake.head.val.x - 1]);
      console.log("FOOOOOOOOD");
