const createSbakeNode = (x, y, direction = "left") => {
  const cord = { x, y };
  const dir = direction;
  return { cord, dir };
};
const moveSnakeNode = (snakeNode, direction = "") => {
  const { x, y } = snakeNode.coord;
  switch (direction.toString().toLowerCase()) {
    case "up":
      return { x, y: y - 1 };
    case "down":
      return { x, y: y + 1 };
    case "left":
      return { x: x - 1, y };
    case "right":
      return { x: x + 1, y };
    default:
      return { x, y };
  }
};

const moveSnake = (snake = [], direction) => {
  snake.map((snakeNode, index) => {
    if (index >= snake.length() - 1) {
      snakeNode.dir = direction;
      snakeNode
    }
  });
};

export class Snake {
  constructor(x, y) {
    this.value = createSbakeNode(x, y);
  }

  grow() {}
}
