import { useState } from "react";
import { Snake } from "../../utils/Snake";
import { StyledBordCell, StyledGameBord } from "./GameBoard.styled";

const createBoard = (BOARD_SIZE) => {
  let count = 1;
  const board = [];
  for (let row = 0; row < BOARD_SIZE; row++) {
    const currentRow = [];
    for (let colum = 0; colum < BOARD_SIZE; colum++) {
      currentRow.push("+");
    }
    board.push(currentRow);
  }
  return board;
};

export const GameBoard = () => {
  const [board, setBoard] = useState(createBoard(10));
  const [snake, setSnake] = useState(new Snake(20));
  console.log(snake);
  return (
    <StyledGameBord>
      {board.map((row, columnIndex) => (
        <div key={columnIndex}>
          {row.map((cellValue, rowIndex) => (
            <StyledBordCell key={columnIndex}>{cellValue}</StyledBordCell>
          ))}
        </div>
      ))}
    </StyledGameBord>
  );
};
